        color: $veryDarkGrayishBlue;
        font-size: $font-md;
        line-height: 1.5rem;
        margin-bottom: 0.75rem;

        @include breakpoint-up(large) {
            font-size: $font-lg;
            line-height: 1.75rem;
            letter-spacing: 0.0156rem;
        }
    }
    &__text {
        color: $desaturatedDarkBlue;
        line-height: 1.25rem;
        letter-spacing: 0.0075rem;
        margin-bottom: 1.25rem;

        @include breakpoint-up(large) {
            margin-bottom: 0.5rem;
        }
    }
    &__profile {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        position: relative;
        padding: 0.75rem 2rem 1.25rem 2rem;
        @include breakpoint-up(large) {
            margin-bottom: 0;
            padding: 0.75rem 0 0 0;
        }
    }
    &__avitar {
        width: 2.5rem;
        border-radius: 50%;
        margin-right: 1rem;
    }
    &__attribution {
        line-height: 1.25rem;
        letter-spacing: 0.0075rem;
    }

    &__author {
        color: $veryDarkGrayishBlue;
    }
    &__date {
        color: $grayishBlue;
    }
    &__share {
        position: relative;
    }
}

.modal {
    background-color: $veryDarkGrayishBlue;
    position: absolute;
    width: 100%;
    height: 100%;
    right: 0;
    top: 0;
    z-index: 10;
    transform: scale(0);
    transition: transform 200ms ease-in-out;
    border-bottom-left-radius: 0.625rem;
    border-bottom-right-radius: 0.625rem;

    @include breakpoint-up(large) {
        top: -4rem;
        right: -6.75rem;
        width: 15.5rem;
        border-radius: 0.625rem;
        box-shadow: 0 0.625rem 0.625rem 0 hsla(210, 29%, 84%, 50%);

        &::before {
            position: absolute;
            content: "";
            height: 1.375rem;
            width: 1.375rem;
            background-color: $veryDarkGrayishBlue;
            bottom: -0.6875rem;
            left: 50%;
            transform: translate(-50%) rotate(45deg);
            transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
        }
    }

    &.active {
        transform: scale(1);
    }

    &__container {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        margin: 0 2rem;

        @include breakpoint-up(large) {
            justify-content: space-between;
            margin: 0 2.25rem;
        }
    }

    &__text {
        line-height: 1.25rem;
        letter-spacing: 0.3125rem;
        color: $grayishBlue;
        margin-right: 1.3125rem;
    }

    &__icons {
        display: flex;

        img:not(:last-of-type) {
            margin-right: 1rem;
        }
    }

    .btn {
        @include breakpoint-up(large) {
            display: none;
        }
    }
}

